import React, { useState, Component } from "react";
import image from "./images/zidane.jpg"

class App extends Component {

    componentWillUnmount() {
        this.setState({
            canvas_img: {
                canvasWidth: 800,
                canvasHeight: 800
            }
        })
    }


    componentDidMount() {
        const ctx = this.canvas_img.getContext("2d");
        const { canvasWidth, canvasHeight } = this.state.canvas_img;

        const img = new Image();
        img.src = image;
        img.onload = () => {
            ctx.drawImage(img, 0, 0, canvasWidth, canvasHeight);
        }

        // Green rectangle
        ctx.beginPath();
        ctx.lineWidth = "4";
        ctx.strokeStyle = "green";
        ctx.rect(3, 3, 50, 50);
        ctx.stroke();
    }

    render() {
        return (
        <div className="App">
            <h1>Hello</h1>
            <canvas id="canvas-img" ref={canvas_img => this.canvas_img = canvas_img}>
            </canvas>
        </div>
        )
    }
}

export default App;
