import React, { useState, Component } from "react";
import image from "./images/zidane.jpg"

class App extends Component {
    componentDidMount() {
        const context = this.canvas_img.getContext("2d");

        const image = new Image();
        image.src = "./images/zidane.jpg"
        image.onload = () => {
            context.drawImage(image, 0, 0, this.canvas_img.width, this.canvas_img.height)
        }
    }

    render() {
        return (
        <div className="App">
            <h1>Hello</h1>
            <canvas id="canvas-img" width="640" height="640" ref={canvas_img => this.canvas_img = canvas_img}>
            </canvas>
        </div>
        )
    }
}

export default App;
