import axios from "axios";
import React, { useEffect, useState } from "react";

import image_resource from "./images/zidane.jpg"

function App() {
  const [url, setUrl] = useState();
  const [image, setImage] = useState();
  const [result, setResult] = useState();

  const image_base = {
    selectedFile: image_resource, /* `null` is a placeholder for the file that will be uploaded. */
    sizeW: 1000,
    sizeH: 1000,
    url: url,
    api_data: null
  }

  function handleChange(event) {
    setImage(event.target.files[0]);
    setUrl(URL.createObjectURL(event.target.files[0]));
  }

  async function onPredict(event) {
    console.log("Result: ", result);
  }

  useEffect(() => {
    // call api
    const formData = new FormData();

    if (image) {
      formData.append(
        'image',
        image,
        image.name
      )

      const url = "http://127.0.0.1:8000/objectdetection";
      setResult(axios.post(url, formData));

      // Clean document
      return () => {

      }
    }
  }, [image])

  return (
    <div className="App">
      <h2>Thành nè</h2>
      <input type="file" onChange={handleChange} />
      <button onClick={onPredict}>Predict!</button>

      <img src={url} alt="" height="480px" />

    </div>
  );
}

export default App;
