import React, { Component } from "react";
import image_resource from "./images/zidane.jpg"
import axios from "axios";

class App extends Component { 
    state = {
        // Initially, no file is selected
        selectedFile: null, /* `null` is a placeholder for the file that will be uploaded. */
        url: null,
        api_data: null
    };



    async componentDidMount() {

        const ctx = this.canvas_img.getContext("2d");

        // load image into canvas-img
        const img = new Image();
        img.src = image_resource;

        const formData = new FormData();

        // Update the formData object
        formData.append(
            "image",
            img.src,
        );

        // Details of the uploaded file
        // console.log(this.state.selectedFile);


        // Request made to the backend api
        // Send formData object
        const url = "http://127.0.0.1:8000/objectdetection"
        const result = await axios.post(url, formData);
        var data = result.data.result
        console.log(data);

        img.onload = () => {
            ctx.drawImage(img, 0, 0, this.canvas_img.width, this.canvas_img.height);

            // draw box
            ctx.beginPath();
            ctx.strokeStyle = 'red';
            ctx.lineWidth = 1;
            ctx.rect(3, 3, 100, 100);
            ctx.stroke();
        }


    }


    render() {
        return (
        <div className="App">
            <h1>Hello</h1>
            <canvas id="canvas-img" width="640" height="640" ref={canvas_img => this.canvas_img = canvas_img}>
            </canvas>
        </div>
        )
    }
}

export default App;
