import axios from "axios";
import React, { useEffect, useState } from "react";

function App() {
  const [url, setUrl] = useState();
  const [image, setImage] = useState();
  const [result, setResult] = useState();

  const base = {
    url: url,
    image: image,
    sizeW: 1000,
    sizeH: 1000,
    result: result,
  }

  function handleChange(event) {
    setImage(event.target.files[0]);
    setUrl(URL.createObjectURL(event.target.files[0]));
  }

  async function onPredict(event) {
    console.log("Result: ", result);

    const {data} = await result.then(result => result);
    console.log(data.result);
  }

  useEffect(() => {
    // call api
    const formData = new FormData();

    if (image) {
      formData.append(
        'image',
        image,
        image.name
      )

      const url = "http://127.0.0.1:8000/objectdetection";
      setResult(axios.post(url, formData));
      console.log("Called API");

      // Clean document
      return () => {

      }
    }
  }, [image])

  useEffect(()=> {
    const ctx = this.canvas_img.getContext("2d");

    // load image into canvas-img
    const img = new Image();
    img.src = this.state.selectedFile;
    this.setState({
        sizeH: img.height,
        sizeW: img.width
    })

  })

  return (
    <div className="App">
      <h2>Thành nè</h2>
      <input type="file" onChange={handleChange} />
      <button onClick={onPredict}>Predict!</button>

      <img src={url} alt="" height="480px" />

      <canvas 
        id="canvas-img"
        width={base.sizeW}
        height={base.sizeH}
      />

    </div>
  );
}

export default App;
