      //   setImgW(img.width);
      // }

      img.addEventListener("load", () => {
        setImgH(img.height);
        setImgW(img.width);
      })
      // img.src = image.url;
      // Cleanup document
      return () => {
        img.removeEventListener("load");
      }

    }
  }

  useEffect(() => {
    call_api();
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [image])

