      ctx.lineWidth = 1;

      // Drawing box
      const boxs = result.data.result
      console.log("Result: ", boxs);
      boxs.map(box => ctx.rect(box.xmin, box.ymin, box.xmax, box.ymax))

      // ctx.rect(10, 10, 100, 100);
      ctx.stroke();
      console.log("DRAWED");
