import axios from "axios";
import React, { useEffect, useState } from "react";

function App() {
  const [url, setUrl] = useState();
  const [image, setImage] = useState();
  const [result, setResult] = useState([]);
  const [imgW, setImgW] = useState(1000);
  const [imgH, setImgH] = useState(1000);

  function handleChange(event) {
    setImage(event.target.files[0]);
    setUrl(URL.createObjectURL(event.target.files[0]));
  }

  async function onPredict(event) {
    console.log("Result: ", result);

    const {data} = await result.then(result => result);
    console.log(data.result);
  }

  useEffect(() => {
    // call api
    const formData = new FormData();

    if (image) {
      formData.append(
        'image',
        image,
        image.name
      )

      const url = "http://127.0.0.1:8000/objectdetection";
      setResult(axios.post(url, formData));
      console.log("Called API");

      // Clean document
      return () => {

      }
    }
  }, [image])

  useEffect(()=> {
    const c = document.getElementById("canvas-img");
    const ctx = c.getContext("2d");
    console.log("ctx: ", ctx);
    // load image into canvas-img
    const img = new Image();
    img.src = image;
    setImgH(img.height);
    setImgW(img.width);



    img.onload = () => {
      // ctx.drawImage(img, 0, 0, this.canvas_img.width, this.canvas_img.height);
      ctx.drawImage(img, 0, 0, imgW, imgH);
      // draw box
      ctx.beginPath();
      ctx.strokeStyle = 'red';
      ctx.lineWidth = 1;

      ctx.rect(10, 10, 100, 100);

      ctx.stroke();
  }

  }, [result])

  return (
    <div className="App">
      <h2>Thành nè</h2>
      <input type="file" onChange={handleChange} />
      <button onClick={onPredict}>Predict!</button>

      <img src={url} alt="" height="480px" />

      <canvas 
        id="canvas-img"
        width={imgW}
        height={imgH}
      />

    </div>
  );
}

export default App;
